export const CHANGE_NAME = "CHANGE_NAME";
export const ADD_MINUTES = "ADD_MINUTES";


function changeName(newName){
    //newName.preventDefault();
    const newCharState = mainCharacter;
    newCharState.name = newName;
    setMainCharacter(newCharState);
  };

  function addLust(addLust){
    const newCharState = mainCharacter;
    if(newCharState.lust + addLust > 100){
      newCharState.lust = 100;
    } else{
      newCharState.lust = newCharState.lust + addLust;
    }
    setMainCharacter(newCharState);
  }

  function setLust(Lust){
    const newCharState = mainCharacter;
    if(Lust > 100){
      newCharState.lust = 100;
    } else{
      newCharState.lust = Lust;
    }
    setMainCharacter(newCharState);
  }

  function addSkills(SkillName,ammount){
    const newCharState = mainCharacter;
    newCharState.skills[SkillName] += ammount;
    setMainCharacter(newCharState);
  }

  function pushFlag(newState){
    const newCharState = mainCharacter;
    newCharState.flags.push(newState);
    setMainCharacter(newCharState);
  }

  function changeSandyRelationship(ChangeAmmount){
    const newCharState = mainCharacter;
    newCharState.relations["Sandy"] += ChangeAmmount
    setMainCharacter(newCharState)
  }

  function changeAmyRelationship(ChangeAmmount){
    const newCharState = mainCharacter;
    newCharState.relations["Amy"] += ChangeAmmount
    setMainCharacter(newCharState)
  }

  
  function pushStatus(newStatus){
    if(!mainCharacter.status.includes(newStatus)){
      const newCharState = mainCharacter;
      newCharState.status.push(newStatus)
      setMainCharacter(newCharState);  
    } 
  }

  
  function spliceStatus(StatusToRemove){
    if(mainCharacter.status.includes(StatusToRemove)){
      const newCharState = mainCharacter;
      newCharState.status.splice(mainCharacter.status.indexOf(StatusToRemove),1)
      setMainCharacter(newCharState);  
    } 
  }
  
  function spliceFlag(FlagToRemove){
    if(mainCharacter.flags.includes(FlagToRemove)){
      const newCharState = mainCharacter;
      newCharState.flags.splice(mainCharacter.flags.indexOf(FlagToRemove),1)
      setMainCharacter(newCharState);  
    } 
  }

  function addGender(ammountToAdd){
    const newCharState = mainCharacter;
    newCharState.gender += ammountToAdd;
    setMainCharacter(newCharState);
  }

  function addChest(ammountToAdd){
    const newCharState = mainCharacter;
    newCharState.chest += ammountToAdd;
    setMainCharacter(newCharState);
  }

  function addHeight(ammountToAdd){
    const newCharState = mainCharacter;
    newCharState.height += ammountToAdd;
    setMainCharacter(newCharState);
  }

  function addBelly(ammountToAdd){
    const newCharState = mainCharacter;
    newCharState.belly += ammountToAdd;
    setMainCharacter(newCharState);
  }

  function addAttractiveness(ammountToAdd){
    const newCharState = mainCharacter;
    newCharState.attractiveness += ammountToAdd;
    setMainCharacter(newCharState);
  }

  //add minutes to the day

  function addItems(itemToAdd, quantity){
    const newCharState = mainCharacter;
    newCharState.items[itemToAdd] += quantity;
    setMainCharacter(newCharState);
  }

  ////

  function removeEnergyFromMinutes(minutes){
    const newCharState = mainCharacter;
    newCharState.energy -= minutes * .09;
    setMainCharacter(newCharState);
  }

  function addMinutes(minutesToAdd){
    let minutes = mainDate.getMinutes()
    mainDate.setMinutes(minutes + minutesToAdd)
    setMainDate(mainDate);
    removeEnergyFromMinutes(minutesToAdd);
  }

  function addHour(hoursToSet){
    mainDate.setHours(hoursToSet);
    setMainDate(mainDate);
  }

  function addEnergy(energyChange){
    const newCharState = mainCharacter;
    newCharState.energy += energyChange
    setMainCharacter(newCharState);
  }
