import mainCharacter from "../data/mainCharacter"
import * as actionTypes from "./actions";
//A reducer is a function
const reducer = (state = mainCharacter, action) =>{
    switch(action.type){
        case actionTypes.CHANGE_NAME:
            return{
                ...state,
                name : action.newName
            }
        case actionTypes.ADD_MINUTES: 
            
            let minutes = state.date.getMinutes();
            let newDate = state.date;
            newDate.setMinutes(minutes + action.minutesToAdd);
            return{
                ...state,
                date : newDate
            }
        default:
            return state;
    }
    
}

export default reducer
