import React from 'react';
import ReactDOM from "react-dom";

import Col1 from "../components/col1";
import Col2 from "../components/col2";
import Col3 from "../components/col3";


//Images
import madrid from '../Images/Banners/madrid.png'
import Window_black_pink_flash from "../Images/Bedroom/Window_black_pink_flash.png"
import myRoom from "../Images/Bedroom/Apartment.jpg";
import anri_emily_lying_down_transparent from '../Images/Characters/Anri-Flat-EM/anri-emily-lying_down_transparent.png';

const Apartment = ( props ) => {
    //20 Mins to get to apmt
    props.addMinutes(20);
    function refreshApartment(){
        Apartment(props);
    }

    if(props.flags.includes("Beginning")){    
        props.spliceFlag("Beginning");
        
        return(
                    <>
                    <Col1 BackImage = {madrid}>
                        <h1 id="col_1_title"> MayVille </h1>
                    </Col1>
                    <Col2 BackImage = {anri_emily_lying_down_transparent}>
                        <h2>Welcome, I hope you enjoy the game.</h2>
                        <p>I plan to build an amazing open-world game for all transformation lovers.</p>
                        <p>This game contains graphic adult content. Only play this if you are 18 or older</p>
                        
                        <button type="button" className="btn btn-primary"  onClick={() => {props.addFlag("0_Clicked_18_or_older");refreshApartment()}}>I can play the game</button>
                    </Col2>
    
                    <Col3 BackImage = {madrid} > 
                    </Col3>
                    </>
                )
                
            
        } else if(props.flags.includes("0_Clicked_18_or_older")){
            props.spliceFlag("0_Clicked_18_or_older");

            function handleNameChange(event){
                console.log(event.target.value)
                props.changeName(event.target.value);
            }

            ReactDOM.render(
                <>
                    <Col1 BackImage = {madrid}>
                    <h1 id="col_1_title"> MayVille </h1>
                    </Col1>
                    <Col2 BackImage = {Window_black_pink_flash}>
                        <p>
                        You are <input type="text" name="name" onChange={handleNameChange}/> You live alone in a small apartment. You are currently unemployed and living from government checks. 
                        </p>
                        <p>One night you see a flash of light through your window.</p>
                    </Col2>

                    <Col3 > 
                        <button type="button" className="btn btn-primary"  onClick={() => {props.addFlag("1_Clicked_inspect");refreshApartment()}}>Inspect</button>
                        <button type="button" className="btn btn-primary"  onClick={() => {props.addFlag("1_Clicked_ignore");refreshApartment()}}>Ignore</button>
                    </Col3>
                    </>, document.getElementById("root")
                )
        }
        
        
        else{
            ReactDOM.render(
                <>
                <Col1 BackImage = {madrid}>
                    <h1 id="col_1_title"> MayVille </h1>
                </Col1>
                <Col2 BackImage = {myRoom}>
                    
                </Col2>

                <Col3 > 
                    <p> You are home {props.name}</p>
                </Col3>
                </>, document.getElementById("root")
                )
            }
    }
