import React from 'react';
import { Link } from 'react-router-dom';
import Col1 from "../../components/col1";
import Col2 from "../../components/col2";
import Col3 from "../../components/col3";
import SetCol1 from "../../components/SetCol1";


//Images
import FarmgirlOwner from "../../Images/Farm/farmgirl_owner.jpg"
import FarmBarn from "../../Images/Farm/farm_barn.jpg"
import FarmCowLyingDown from "../../Images/Farm/farm_cow_lying_down.jpg"
import FarmStoreImage from "../../Images/Farm/FarmStore.jpg"


const TheBarn = ( props ) => {

    const addMinutesFunc = () =>{
        props.onAddMinutes(10)
    }

    if(props.flags.includes("FARM_UNVISITED")){
        props.onSpliceFlag("FARM_UNVISITED");
        return(
            <>
            <Col1>
                <SetCol1
                money = {props.money}
                energy = {props.energy}
                date = {props.date}
                />
            </Col1>
            <Col2 BackImage = {FarmgirlOwner}>
                
            </Col2>
    
            <Col3 > 
                <p>"Welcome!" You hear someone say. "I have not seen you around before, do you need any help?"</p>
                
                <button type="button" className="btn btn-primary" id ="_8_farmHelp" onClick={_8_farm_help}></button>
                <button type="button" className="btn btn-primary" id ="_8_noFarmHelp" onClick={_8_noFarmHelp}></button>           
                
                <Link to={"/HowDoesTheFarmWork"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary" onClick ={addMinutesFunc}>Yes, how does this farm work?</button>
                </Link>
                <Link to={"/TheBarn"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary">No, I don't need help</button>
                </Link>
    
            </Col3>
            </>
            )
    
    } else if (props.flags.includes("LacBraQSandyInFarm")){
        props.onSpliceFlag("LacBraQSandyInFarm");
        
        if(Math.random() < .65){
            props.onPushFlag("WillMeetSandyOutsideApartment")
        } else{
            props.onPushFlag("WillMeetAmyOutsideApartment")
        }

        return(
            <>
            <Col1>
                <SetCol1
                money = {props.money}
                energy = {props.energy}
                date = {props.date}
                />
            </Col1>
            <Col2 BackImage = {Bedroom}>
                
            </Col2>
    
            <Col3 > 
                <p>
                    You hear unusual movement outside your apartment.
                </p>
                
                <Link to={"/GoOutside"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary">Go outside</button>
                </Link>
    
            </Col3>
            </>
            )
    }
    
    
    
    
    else{
        return(
            <>
            <Col1>
                <SetCol1
                money = {props.money}
                energy = {props.energy}
                date = {props.date}
                />
            </Col1>
            <Col2 BackImage = {Bedroom}>
                
            </Col2>
    
            <Col3 > 
                <p>
                    You are home.
                </p>
    
                <Link to={"/Apartment"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary">The next morining</button>
                </Link>
                <Link to={"/Apartment"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary">The next morining</button>
                </Link>
                <Link to={"/Apartment"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary">The next morining</button>
                </Link>
    
            </Col3>
            </>
            )
    }


    



    }
export default TheBarn;
