import React from 'react';
import ReactDOM from "react-dom";

import Col1 from "../components/col1";
import Col2 from "../components/col2";
import Col3 from "../components/col3";

import {connect} from "react-redux";

import * as actionTypes from "../redux/actions";

//Images
import madrid from '../Images/Banners/madrid.png'
import Window_black_pink_flash from "../Images/Bedroom/Window_black_pink_flash.png"
import myRoom from "../Images/Bedroom/Apartment.jpg";
import anri_emily_lying_down_transparent from '../Images/Characters/Anri-Flat-EM/anri-emily-lying_down_transparent.png';

const Apartment = ( props ) => {
    //20 Mins to get to apmt
    props.onAddMinutes(20);
    console.log(props.date.toString())

    if(props.flags.includes("Beginning")){    
        props.onSpliceFlag("Beginning");
        
        return(
                    <>
                    <Col1 BackImage = {madrid}>
                        <h1 id="col_1_title"> MayVille </h1>
                    </Col1>
                    <Col2 BackImage = {anri_emily_lying_down_transparent}>
                        <h2>Welcome, I hope you enjoy the game.</h2>
                        <p>I plan to build an amazing open-world game for all transformation lovers.</p>
                        <p>This game contains graphic adult content. Only play this if you are 18 or older</p>
                        
                        <button type="button" className="btn btn-primary"  onClick={() => {props.onPushFlag("0_Clicked_18_or_older");Apartment(props)}}>I can play the game</button>
                    </Col2>
    
                    <Col3 BackImage = {madrid} > 
                    </Col3>
                    </>
                )
                
            
        } else if(props.flags.includes("0_Clicked_18_or_older")){
            props.onSpliceFlag("0_Clicked_18_or_older");

            function handleNameChange(event){
                console.log(event.target.value);
                props.onNameChange(event.target.value);
                console.log(props.myName)
                
            }

            ReactDOM.render(
                <>
                    <Col1 BackImage = {madrid}>
                    <h1 id="col_1_title"> MayVille </h1>
                    </Col1>
                    <Col2 BackImage = {Window_black_pink_flash}>
                        <p>
                        You are <input type="text" name="name" onChange={handleNameChange}/> You live alone in a small apartment. You are currently unemployed and living from government checks. 
                        </p>
                        <p>One night you see a flash of light through your window.</p>
                    </Col2>

                    <Col3 > 
                        <button type="button" className="btn btn-primary"  onClick={() => {props.onPushFlag("1_Clicked_inspect");}}>Inspect</button>
                        <button type="button" className="btn btn-primary"  onClick={() => {props.onPushFlag("1_Clicked_ignore");}}>Ignore</button>
                    </Col3>
                    </>,document.getElementById("root")
                )
        }
        
        
        else{
            return(
                <>
                <Col1 BackImage = {madrid}>
                    <h1 id="col_1_title"> MayVille </h1>
                </Col1>
                <Col2 BackImage = {myRoom}>
                    
                </Col2>

                <Col3 > 
                    <p> You are home {props.myName}</p>
                </Col3>
                </>
                )
            }
}



// export default Apartment;

const mapStateToProps = state =>{
    return{
      myName: state.name,
      date: state.date,
      lust: state.lust,
      skills: state.skills,
      physical: state.physical,
      mind: state.mind,
      attractiveness: state.attractiveness,
      pronoun : state.pronoun,
      energy: state.energy,
      chest: state.chest,
      belly: state.belly,
      height: state.height,
      status: state.status,
      environmentStatus: state.environmentStatus,
      items: state.items,
      money: state.money,
      flags: state.flags,
      relations: state.relations
    };
  }
  
  const mapDispatchToProps = dispatch =>{
    return{
      onNameChange: (nameChange) => dispatch({type: actionTypes.CHANGE_NAME, newName:nameChange}),
      onAddMinutes: (minutes) => dispatch({type: actionTypes.ADD_MINUTES, minutesToAdd:minutes}),
      onAddLust: (lust) => dispatch({type: actionTypes.ADD_LUST, lustToAdd:lust}),
      onSetLust: () => dispatch({type: actionTypes.SET_LUST}),
      onAddSkills: (_skillToAdd, _skillAmmountToAdd) => dispatch({type: actionTypes.ADD_SKILLS, skillToAdd: _skillToAdd, skillAmmountToAdd: _skillAmmountToAdd}),
      onPushFlag: (_flagToPush) => dispatch({type: actionTypes.PUSH_FLAG,flagToAdd: _flagToPush }),
      onChangeRelationship: (_relationPerson, _relationAmmountToAdd) => dispatch ({type: actionTypes.CHANGE_RELATIONSHIP, relationPerson: _relationPerson, relationAmmountToAdd: _relationAmmountToAdd}),
      onPushStatus : (_statusToPush) => dispatch({type: actionTypes.PUSH_STATUS, statusToAdd: _statusToPush}),
      onSpliceStatus : (_statusToSplice) => dispatch({type: actionTypes.SPLICE_STATUS, statusToRemove: _statusToSplice}),
      onSpliceFlag: (_flagToRemove) => dispatch({type: actionTypes.SPLICE_FLAG, flagToRemove: _flagToRemove}),
      onAddChest: (_chestToAdd) => dispatch({type: actionTypes.ADD_CHEST,
      chestToAdd: _chestToAdd}),
      onAddHeight: (_heightToAdd) => dispatch({type: actionTypes.ADD_HEIGHT, heightToAdd:_heightToAdd}),
      onAddBelly: (_bellyToAdd) => dispatch({type: actionTypes.ADD_BELLY, bellyToAdd:_bellyToAdd}),
      onAddItem: (_itemToAdd, _itemAmmountToAdd) => dispatch({type: actionTypes.ADD_ITEMS, itemToAdd: _itemToAdd, itemAmmountToAdd: _itemAmmountToAdd}),
      onEnergyAndMinutes: (_minutesChanged) => dispatch({type: actionTypes.ENERGY_AND_MINUTES, minutesChanged: _minutesChanged}),
      onSetHour: (_hourToSet) => dispatch({type:actionTypes.SET_HOUR,hourToSet: _hourToSet})
  
  
      
    }
  }
  
  export default connect(mapStateToProps,mapDispatchToProps)(Apartment);
