import './App.css';
import Main from "./Main"
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {changeName} from "./redux/actions";
import * as actionTypes from "./redux/actions"

const App = ( props ) => {


  return( 
      <div>
          <p>{props.name}</p>
      </div>
  );

}

const mapStateToProps = state =>{
  return{
    name: state.mainCharacter.name
  }
}

const mapDispatchToProps = dispatch =>{
  return{
    onNameChange: (newName) => dispatch({type: actionTypes.CHANGE_NAME, name:newName}),
    onMinuteAdd: (minutes) => dispatch({type: actionTypes.ADD_MINUTES, minutesToAdd:minutes})
  }
}

export default connect(mapStateToProps,mapStateToProps)(App);
