import React from 'react';
import { Link } from 'react-router-dom';

import Col1 from "../../components/col1";
import Col2 from "../../components/col2";
import Col3 from "../../components/col3";
import SetCol1 from "../../components/SetCol1";


//Images
import Bedroom from "../../Images/Bedroom/Apartment.jpg";

const SleepAtHome = ( props ) => {

    const addMinutesFunc = () =>{
        props.onAddMinutes(10); 
    }

    let NoMoreEnergyClick = () =>{
        props.onAddMinutes(480); 
        props.onSetEnergy(75);
    }

    console.log(props.flags)

    if(props.flags.includes("NO_MORE_ENERGY")){
        props.onSpliceFlag("NO_MORE_ENERGY");

        return(
        <>
        <Col1>
            <SetCol1
                money = {props.money}
                energy = {props.energy}
                date = {props.date}
            />
        </Col1>
        <Col2 BackImage = {Bedroom}>            
        </Col2>

        <Col3 > 
        
             <p>You had no more energy. So you ran home and fell asleep. You will not sleep well.</p>
              <Link to={"/SleepAtHome"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary" onClick ={NoMoreEnergyClick}>Fall asleep</button>
              </Link>

        </Col3>
        </>
        )
    } else{
        props.onPushFlag("MET_AMY");
    return(
        <>
        <Col1>
            <SetCol1
                money = {props.money}
                energy = {props.energy}
                date = {props.date}
            />
        </Col1>
        <Col2 BackImage = {BegginingAmyOutside}>            
        </Col2>

        <Col3 > 
                <p>Hue</p>
              
              <Link to={"/LacBraQAmyMovingIn"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary" onClick ={OfferedHelpToMoveBoxes}>Help Amy move the boxes</button>
              </Link>
              

              <Link to={"/GameMap"} style={{ textDecoration: "none" }}>
                    <button type="button" className="btn btn-primary" onClick ={addMinutesFunc}>Leave</button>
              </Link>

        </Col3>
        </>
        )
    }
}


    

export default SleepAtHome;
